package com.onyx.kreader.host.request;import android.graphics.Bitmap;import com.onyx.android.sdk.data.model.Annotation;import com.onyx.android.sdk.utils.FileUtils;import com.onyx.kreader.R;import com.onyx.kreader.common.BaseReaderRequest;import com.onyx.kreader.host.wrapper.Reader;import java.io.File;import java.util.List;/** * Created by ming on 2016/10/28. */public class ExportScribbleRequest extends BaseReaderRequest {    private Bitmap bitmap;    private String page;    public ExportScribbleRequest(Bitmap bitmap, String page) {        this.bitmap = bitmap;        this.page = page;    }    @Override    public void execute(Reader reader) throws Exception {        String baseName = FileUtils.getBaseName(reader.getDocumentPath());        File file = new File(reader.getExportDictPath(), baseName + "-scribble-" + page + ".png");        FileUtils.saveBitmapToFile(bitmap, file, Bitmap.CompressFormat.PNG, 100);    }}