package com.onyx.kreader.host.request;import android.graphics.Typeface;import com.onyx.android.sdk.common.request.BaseRequest;import com.onyx.android.sdk.data.FontInfo;import com.onyx.android.sdk.data.GAdapter;import com.onyx.android.sdk.data.GAdapterUtil;import com.onyx.android.sdk.data.GObject;import com.onyx.kreader.common.BaseReaderRequest;import com.onyx.kreader.host.wrapper.Reader;import com.onyx.kreader.ui.actions.BaseAction;import com.onyx.kreader.utils.DeviceUtils;import java.util.ArrayList;import java.util.List;/** * Created by ming on 2016/11/18. */public class GetFontsRequest extends BaseReaderRequest {    private List<FontInfo> fonts;    private String currentFont;    public GetFontsRequest(String currentFont) {        this.currentFont = currentFont;    }    @Override    public void execute(Reader reader) throws Exception {        fonts = DeviceUtils.buildFontItemAdapter(currentFont, null);    }    public List<FontInfo> getFonts() {        return fonts;    }}