package com.onyx.kreader.utils;import com.onyx.android.sdk.utils.FileUtils;import java.io.File;/** * Created by ming on 2016/10/31. */public class ExportUtils {    private static String getExportFolderPath(String documentPath) {        String parent = FileUtils.getParent(documentPath);        String baseName = FileUtils.getBaseName(documentPath);        String path = parent + "/" + baseName;        FileUtils.createPath(path);        return path;    }    public static String getExportPdfPath(String documentPath) {        String baseName = FileUtils.getBaseName(documentPath);        String ext = FileUtils.getFileExtension(documentPath);        return new File(getExportFolderPath(documentPath), baseName + "-Exported." + ext).getAbsolutePath();    }    public static String getExportAnnotationPath(String documentPath) {        String baseName = FileUtils.getBaseName(documentPath);        return new File(getExportFolderPath(documentPath), baseName + "-annotation.txt").getAbsolutePath();    }    public static String getExportScribblePath(String documentPath, String page) {        String baseName = FileUtils.getBaseName(documentPath);        return new File(getExportFolderPath(documentPath), baseName + "-scribble-" + page + ".png").getAbsolutePath();    }}