package com.onyx.libedu.model;import android.content.Context;import com.onyx.android.sdk.data.model.BaseData;import java.util.ArrayList;import java.util.List;/** * Created by ming on 2016/11/2. */public class Difficult extends BaseData{    private String difficultName;    public String getDifficultName() {        return difficultName;    }    public void setDifficultName(String difficultName) {        this.difficultName = difficultName;    }    public static Difficult create(long id, String difficultName) {        Difficult difficult = new Difficult();        difficult.setDifficultName(difficultName);        difficult.setId(id);        return difficult;    }    public static List<Difficult> createCommonDifficults(Context context) {        List<Difficult> difficults = new ArrayList<>();        difficults.add(create(0, "全部"));        difficults.add(create(1, "容易"));        difficults.add(create(2, "较易"));        difficults.add(create(3, "一般"));        difficults.add(create(4, "较难"));        difficults.add(create(5, "困难"));        return difficults;    }}