package com.onyx.libedu.model;import android.content.Context;import com.onyx.android.sdk.data.model.BaseData;import java.util.ArrayList;import java.util.List;/** * Created by ming on 2016/11/1. */public class Stage extends BaseData {    private String stageName;    public String getStageName() {        return stageName;    }    public void setStageName(String stageName) {        this.stageName = stageName;    }    public static Stage create(long id, String stageName) {        Stage stage = new Stage();        stage.setStageName(stageName);        stage.setId(id);        return stage;    }    public static List<Stage> createCommonStages(Context context) {        List<Stage> stages = new ArrayList<>();        stages.add(create(1, "小学"));        stages.add(create(2, "初中"));        stages.add(create(3, "高中"));        return stages;    }}