package com.onyx.libedu.request.cloud;import com.onyx.android.sdk.data.utils.JSONObjectParseUtils;import com.onyx.libedu.BaseEduRequest;import com.onyx.libedu.EduCloudManager;import com.onyx.libedu.EduServiceFactory;import com.onyx.libedu.model.Result;import com.onyx.libedu.model.Subject;import org.json.JSONObject;import java.util.List;import java.util.Map;import retrofit2.Call;import retrofit2.Response;/** * Created by ming on 2016/10/31. */public class GetSubjectRequest extends BaseEduRequest{    private static final String TAG = "GetSubjectRequest";    private List<Subject> subjects;    private long stage;    public GetSubjectRequest(long stage) {        this.stage = stage;    }    @Override    public void execute(EduCloudManager parent) throws Exception {        Map<String, Object> paramMap = parent.createParamMap();        paramMap.put("stage", stage);        parent.sign(paramMap);        Call<Result<List<Subject>>> call = EduServiceFactory.getEduService(parent.getEduConf().getApiBase()).getSubjectList(paramMap);        Response<Result<List<Subject>>> response = call.execute();        if (response.isSuccessful()) {            Result<List<Subject>> result = response.body();            subjects = result.getData();        } else {            String errorCode = JSONObjectParseUtils.httpStatus(response.code(), new JSONObject(response.errorBody().string()));            throw new Exception(errorCode);        }    }    public List<Subject> getSubjects() {        return subjects;    }}