package com.onyx.libedu.request.cloud;import com.onyx.android.sdk.data.utils.JSONObjectParseUtils;import com.onyx.libedu.BaseEduRequest;import com.onyx.libedu.EduCloudManager;import com.onyx.libedu.EduServiceFactory;import com.onyx.libedu.model.Result;import com.onyx.libedu.model.Subject;import com.onyx.libedu.model.Version;import org.json.JSONObject;import java.util.List;import java.util.Map;import retrofit2.Call;import retrofit2.Response;/** * Created by ming on 2016/11/1. */public class GetVersionsRequest extends BaseEduRequest{    private long subjectId;    private List<Version> versions;    public GetVersionsRequest(long subjectId) {        this.subjectId = subjectId;    }    @Override    public void execute(EduCloudManager parent) throws Exception {        Map<String, Object> paramMap = parent.createParamMap();        paramMap.put("subjectId", subjectId);        parent.sign(paramMap);        Call<Result<List<Version>>> call = EduServiceFactory.getEduService(parent.getEduConf().getApiBase()).getVersionList(paramMap);        Response<Result<List<Version>>> response = call.execute();        if (response.isSuccessful()) {            Result<List<Version>> result = response.body();            versions = result.getData();        } else {            String errorCode = JSONObjectParseUtils.httpStatus(response.code(), new JSONObject(response.errorBody().string()));            throw new Exception(errorCode);        }    }    public List<Version> getVersions() {        return versions;    }}